import React, { useState } from "react";
import { GlobalStyle, AppContainer, Column, Row, Button } from "../styles";

const Home = () => {
  const [buttonText, setButtonText] = useState("|");
  const [isLeftBlockVisible, setIsLeftBlockVisible] = useState(true);

  const handleMouseOver = () => setButtonText("⟨");
  const handleMouseOut = () => setButtonText("|");
  const toggleLeftBlock = () => setIsLeftBlockVisible(!isLeftBlockVisible);

  return (
    <>
      <GlobalStyle />
      <AppContainer>
        <Column
          flex="0 0 230px"
          backgroundColor="#1c1c1c"
          display={isLeftBlockVisible ? "block" : "none"}
        >
          <Row flex="0 0 30px"> <img src="Logo.png" width="160px" style={{paddingTop: "13px"}}></img> </Row>
          <Row flexGrow={1} />
          <Row flex="0 0 30px" />
        </Column>
        <Column flex="1" style={{padding: "35px !important"}}>
          <Row style={{height:"47px"}}>
            <Column flex="0 0 20px">
              <Button
                onMouseOver={handleMouseOver}
                onMouseOut={handleMouseOut}
                onClick={toggleLeftBlock}
              >
                {buttonText}
              </Button>
            </Column>
            <Column flexGrow={1} />
          </Row>
          <Row flexGrow={1} >
          <textarea class="memo" style={{fontSize: "17px", color: "whitesmoke", margin: "45px", padding: "15px", height: "100%", width: "100%", background: "#2c2c2c",outline: "none", borderRadius: "30px", resize: "none", boxShadow: "10px 0 10px rgba(0.1, 0.1, 0.1, 0.1)"}}>Мені тринадцятий минало, купив собі я жигулі,&#10;Мені так любо любо стало, що я заїхав у таксі,&#13;&#10;Мене міліція шукає,а я сиджу у гаражі,&#13;&#10;Останю пляшку допиваю і ремонтую жигулі.</textarea>
          </Row>
          <Row>Row 3</Row>
        </Column>
      </AppContainer>
    </>
  );
}

export default Home;
