import React, { useState } from 'react';
import './App.css';

function App() {
  // Стан для тексту кнопки
  const [buttonText, setButtonText] = useState('|');

  // Стан для відображення блоку
  const [isBlockVisible, setIsBlockVisible] = useState(true);

  // Функція для перемикання тексту кнопки
  const handleMouseOver = () => setButtonText('⟨');
  const handleMouseOut = () => setButtonText('|');

  // Функція для перемикання видимості блоку
  const toggleBlock = () => setIsBlockVisible(!isBlockVisible);

  return (
    <div className="App">
      <button
        className="btnAnim"
        onMouseOver={handleMouseOver}
        onMouseOut={handleMouseOut}
        onClick={toggleBlock}
      >
        {buttonText}
      </button>

      {/* Блок, видимість якого перемикається */}
      <div
        id="leftColumn"
        style={{ display: isBlockVisible ? 'block' : 'none' }}
      >
        Це контент блоку!
      </div>
    </div>
  );
}

export default App;
