import React, { useState } from 'react';
import './App.css';

function App() {
  const [buttonText, setButtonText] = useState('|');
  const [isLeftBlockVisible, setIsLeftBlockVisible] = useState(true);

  const handleMouseOver = () => setButtonText('⟨');
  const handleMouseOut = () => setButtonText('|');
  const toggleLeftBlock = () => setIsLeftBlockVisible(!isLeftBlockVisible);

  return (
    <div style={{ display: 'flex', height: '100vh', width: '100vh' }}>
      {isLeftBlockVisible && (
        <div
          style={{
            flex: '0 0 230px',
            backgroundColor: '#1c1c1c',
            display: 'block',
          }}
        >
          <div style={{ flex: '0 0 30px' }}></div>
          <div style={{ flexGrow: 1 }}></div>
          <div style={{ flex: '0 0 30px' }}></div>
        </div>
      )}
      <div style={{ flexGrow: 1 }}>
        <div style={{ display: 'flex' }}>
          <div style={{ flex: '0 0 20px' }}>
            <button
              onMouseOver={handleMouseOver}
              onMouseOut={handleMouseOut}
              onClick={toggleLeftBlock}
            >
              {buttonText}
            </button>
          </div>
          <div style={{ flexGrow: 1 }}></div>
        </div>
        <div>Row 2</div>
        <div>Row 3</div>
      </div>
    </div>
  );
}
