import React, { useState } from "react";
import { GlobalStyle, AppContainer, Column, Row, Button } from "../styles";

const Home = () => {
  const [buttonText, setButtonText] = useState("|");
  const [isLeftBlockVisible, setIsLeftBlockVisible] = useState(true);

  const handleMouseOver = () => setButtonText("⟨");
  const handleMouseOut = () => setButtonText("|");
  const toggleLeftBlock = () => setIsLeftBlockVisible(!isLeftBlockVisible);

  return (
    <>
      <GlobalStyle />
      <AppContainer>
        <Column
          flex="0 0 230px"
          backgroundColor="#1c1c1c"
          display={isLeftBlockVisible ? "block" : "none"}
        >
          <Row flex="0 0 30px"> <img src="Logo.png" width="160px" style={{paddingTop: "13px"}}></img> </Row>
          <Row flexGrow={1} />
          <Row flex="0 0 30px" />
        </Column>
        <Column flex="1" style={{padding: "35px !important"}}>
          <Row style={{height:"47px"}}>
            <Column flex="0 0 20px">
              <Button
                onMouseOver={handleMouseOver}
                onMouseOut={handleMouseOut}
                onClick={toggleLeftBlock}
              >
                {buttonText}
              </Button>
            </Column>
            <Column flexGrow={1} />
          </Row>
          <Row flexGrow={1} >
          <textarea class="memo" placeholder="Введи свій текст тут..."></textarea>
          </Row>
          <Row>Row 3</Row>
        </Column>
      </AppContainer>
    </>
  );
}

export default Home;
