import styled, { createGlobalStyle } from "styled-components";

export const GlobalStyle = createGlobalStyle`
  body, html {
    position: relative;
    margin: 0;
    display: flex;
    height: 100vh;
    background: #2c2c2c;
    color: #dbdbdb;
    font-family: Arial;
    font-size: 12pt;
    width: 100vw;
    border-radius: 20px;
    overflow: hidden;
  }

  body::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border: 0.5px solid #35383f;
    border-radius: 20px;
    pointer-events: none;
    box-sizing: border-box;
    overflow: hidden;
  }
`;

export const Button = styled.button`
  background-color: transparent;
  border: none;
  cursor: pointer;
  position: relative;
  color: #dadada;
  font-size: 19px;
  font-weight: bold;
  padding: 10px 15px;
`;

export const AppContainer = styled.div`
  display: flex;
`;

export const Column = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  padding: 10px;
  word-break: break-word;
  overflow-wrap: break-word;
`;

export const Row = styled.div`
  display: flex;
`;

export const LeftBorder = styled.div`
  position: relative;

  &::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    border-left: 0.5px solid #35383f;
    pointer-events: none;
    box-sizing: border-box;
  }
`;
