import React, { useState, useEffect } from "react";
import ReactDOM from "react-dom/client";

function ButtonLogic() {
  const [buttonText, setButtonText] = useState("|");
  const [isLeftColumnVisible, setLeftColumnVisible] = useState(false);

  // Показуємо або ховаємо блок через клас чи стиль
  useEffect(() => {
    const leftColumn = document.getElementById("leftColumn");
    if (leftColumn) {
      leftColumn.style.display = isLeftColumnVisible ? "block" : "none";
    }
  }, [isLeftColumnVisible]);

  const handleMouseOver = () => setButtonText("⟨");
  const handleMouseOut = () => setButtonText("|");
  const toggleLeftColumn = () => setLeftColumnVisible(!isLeftColumnVisible);

  return (
    <>
      <button
        className="btnAnim"
        onMouseOver={handleMouseOver}
        onMouseOut={handleMouseOut}
        onClick={toggleLeftColumn}
      >
        {buttonText}
      </button>
    </>
  );
}

// Під’єднуємо React до існуючого HTML елемента
const root = ReactDOM.createRoot(document.getElementById("toggleButton"));
root.render(<ButtonLogic />);
