import React, { useState } from 'react';
import { GlobalStyle } from "./styles";
import './App.css';

function App() {
  const [buttonText, setButtonText] = useState('|');
  const [isLeftBlockVisible, setIsLeftBlockVisible] = useState(true);
  const handleMouseOver = () => setButtonText('⟨');
  const handleMouseOut = () => setButtonText('|');
  const toggleLeftBlock = () => setIsLeftBlockVisible(!isLeftBlockVisible);

  return (
    <>
      <GlobalStyle />
    <div className="App" style={{ display: 'flex', height: '100vh', width: '100vh' }}>
      <div
        className="col"
        id="leftColumn"
        style={{
          flex: '0 0 230px',
          backgroundColor: '#1c1c1c',
          display: isLeftBlockVisible ? 'block' : 'none', }}
      >
        <div className="row" style={{ flex: '0 0 30px'}}></div>
        <div className="row" style={{ flexGrow: 1 }}></div>
        <div className="row" style={{ flex: '0 0 30px' }}>
        </div>
      </div>

      <div className="col" id="mainColumn" style={{ flexGrow: 1}}>
        <div className="headerPanel row">
          <div className="col" style={{ flex: '0 0 20px' }}>
            <left>
              <button
                className="btnAnim"
                id="toggleButton"
                onMouseOver={handleMouseOver}
                onMouseOut={handleMouseOut}
                onClick={toggleLeftBlock}
              >
                {buttonText}
              </button>
            </left>
          </div>
          <div className="col" style={{ flexGrow: 1 }}></div>
        </div>
        <div className="chatPanel row">Row 2</div>
        <div className="fieldPanel row">Row 3</div>
      </div>
    </div>
    ></>
  );
}

export default App;
