import React, { useState } from "react";
import { GlobalStyle, AppContainer, Column, Row, ButtonRegLogin, Input } from "../styles";
import { auth, database } from "./firebase";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { ref, set } from "firebase/database";

const Registration = () => {
  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const userId = userCredential.user.uid;

      await set(ref(database, `users/${userId}`), {
        username,
        email,
      });

      alert("Реєстрація успішна!");
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <>
      <GlobalStyle />
      <AppContainer>
        <Column flex="0 0 650px" backgroundColor="#2c2c2c" style={{
          borderTopRightRadius: "70px",
          borderBottomRightRadius: "70px",
          boxShadow: "10px 0 10px rgba(0, 0, 0, 0.1)"
        }}>
          <Row flex="0 0 30px"></Row>
          <Row flexGrow={1} style={{
                alignItems: "center",
                flexDirection: "column",
                justifyContent: "center"
          }}>
            <div style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
              <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
                <label>Ім'я користувача:</label>
                <Input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
                <label>Email:</label>
                <Input
                  type="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
              </div>
              <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
                <label>Пароль:</label>
                <Input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              <ButtonRegLogin type="submit">Зареєструватися</ButtonRegLogin>
            </div>
          </Row>
          <Row flex="0 0 30px" />
        </Column>
        <Column flexGrow={1} style={{
          paddingLeft: "100px",
          paddingRight: "100px",
          paddingTop: "17px",
          paddingBottom: "17px"
        }}>
          <img src="mscreen.png" style={{
            height: "100%",
            objectFit: "cover"
          }}/>
        </Column>
      </AppContainer>
    </>
  );
};

export default Registration;
