import React, { useState, useEffect } from 'react';
import ReactDOM from 'react-dom/client';
import './index.css';
import reportWebVitals from './reportWebVitals';

function App() {
  const [buttonText, setButtonText] = useState('|');
  const [isLeftColumnVisible, setLeftColumnVisible] = useState(false);

  // Оновлюємо відображення елемента у DOM
  useEffect(() => {
    const leftColumn = document.getElementById('leftColumn');
    if (leftColumn) {
      leftColumn.style.display = isLeftColumnVisible ? 'block' : 'none';
    }
  }, [isLeftColumnVisible]);

  const handleMouseOver = () => setButtonText('⟨');
  const handleMouseOut = () => setButtonText('|');
  const toggleLeftColumn = () => setLeftColumnVisible(!isLeftColumnVisible);

  return (
    <button
      id="toggleButton"
      className="btnAnim"
      onMouseOver={handleMouseOver}
      onMouseOut={handleMouseOut}
      onClick={toggleLeftColumn}
    >
      {buttonText}
    </button>
  );
}

// Підключення React до root-елемента
const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

reportWebVitals();
